import{_ as t,K as d,L as r,W as a}from"./framework-edebdfe1.js";const e={},h=a('<h1 id="合规标准" tabindex="-1"><a class="header-anchor" href="#合规标准" aria-hidden="true">#</a> 合规标准</h1><h2 id="一、ciso-核心理解" tabindex="-1"><a class="header-anchor" href="#一、ciso-核心理解" aria-hidden="true">#</a> 一、CISO 核心理解</h2><ul><li><strong>SOC 2</strong>：信任背书，审计报告</li><li><strong>GDPR</strong>：法律底线，数据保护</li><li><strong>等保</strong>：监管红线，中国境内系统必须</li><li><strong>NIST</strong>：内功方法论，指导安全设计</li></ul><h2 id="二、国际通用审计-安全标准" tabindex="-1"><a class="header-anchor" href="#二、国际通用审计-安全标准" aria-hidden="true">#</a> 二、国际通用审计 &amp; 安全标准</h2><table><thead><tr><th>标准/制度</th><th>全称</th><th>属性</th><th>适用对象</th><th>核心关注点</th></tr></thead><tbody><tr><td>ISO 27001</td><td>信息安全管理体系</td><td>国际标准</td><td>各行业</td><td>安全管理体系（ISMS）、风险管理</td></tr><tr><td>ISO 27701</td><td>隐私信息管理体系</td><td>国际标准</td><td>各行业</td><td>隐私 &amp; 数据保护</td></tr><tr><td>SOC 1</td><td>Service Organization Control 1</td><td>审计</td><td>金融 / 会计相关服务</td><td>财务报告相关控制</td></tr><tr><td>SOC 2</td><td>同上</td><td>审计</td><td>SaaS / 云服务</td><td>信任服务原则（安全、可用性、保密性、完整性、隐私）</td></tr><tr><td>SOC 3</td><td>同上</td><td>审计（公开版）</td><td>SaaS / 云服务</td><td>SOC 2 精简公开版</td></tr><tr><td>PCI DSS</td><td>Payment Card Industry Data Security Standard</td><td>行业标准</td><td>支付 / 电商</td><td>支付卡数据安全</td></tr><tr><td>NIST CSF / SP 系列</td><td>国家标准与技术研究院框架</td><td>方法论</td><td>政府 / 企业</td><td>风险管理、信息安全控制</td></tr><tr><td>CIS Controls</td><td>Center for Internet Security Controls</td><td>实践框架</td><td>各行业</td><td>20 个关键安全控制</td></tr></tbody></table><hr><h2 id="三、法律与监管合规类-强制" tabindex="-1"><a class="header-anchor" href="#三、法律与监管合规类-强制" aria-hidden="true">#</a> 三、法律与监管合规类（强制）</h2><table><thead><tr><th>法规/制度</th><th>国家 / 地区</th><th>核心内容</th><th>适用对象</th></tr></thead><tbody><tr><td>GDPR</td><td>欧盟</td><td>个人数据保护</td><td>涉及欧盟用户的企业</td></tr><tr><td>CCPA / CPRA</td><td>美国加州</td><td>个人信息保护</td><td>面向加州用户的企业</td></tr><tr><td>LGPD</td><td>巴西</td><td>数据保护法</td><td>涉及巴西用户</td></tr><tr><td>HIPAA</td><td>美国</td><td>健康信息保护</td><td>医疗 / 健康数据企业</td></tr><tr><td>FISMA</td><td>美国</td><td>联邦政府信息系统安全</td><td>政府承包商</td></tr><tr><td>等保 2.0</td><td>中国</td><td>信息系统等级保护</td><td>中国境内信息系统</td></tr></tbody></table><hr><h2 id="四、行业-特定功能合规" tabindex="-1"><a class="header-anchor" href="#四、行业-特定功能合规" aria-hidden="true">#</a> 四、行业 / 特定功能合规</h2><table><thead><tr><th>标准/制度</th><th>核心方向</th><th>适用行业</th></tr></thead><tbody><tr><td>SOX（萨班斯-奥克斯利法案）</td><td>财务数据审计</td><td>上市公司（美国）</td></tr><tr><td>FedRAMP</td><td>云安全认证</td><td>政府云服务供应商</td></tr><tr><td>HITRUST</td><td>健康信息安全</td><td>医疗 / 健康数据</td></tr><tr><td>ISO 22301</td><td>业务连续性管理</td><td>所有行业，尤其金融 / 运营关键企业</td></tr><tr><td>ISO 31000</td><td>风险管理</td><td>企业整体风险管理</td></tr><tr><td>TISAX</td><td>汽车行业信息安全</td><td>汽车制造与供应链</td></tr><tr><td>CSA STAR</td><td>云安全联盟</td><td>云服务安全评估 &amp; 透明度</td></tr></tbody></table><hr><h2 id="五、方法论-内部安全框架" tabindex="-1"><a class="header-anchor" href="#五、方法论-内部安全框架" aria-hidden="true">#</a> 五、方法论 / 内部安全框架</h2><table><thead><tr><th>框架</th><th>核心内容</th><th>适用对象</th></tr></thead><tbody><tr><td>NIST CSF</td><td>Identify / Protect / Detect / Respond / Recover</td><td>政府 / 企业</td></tr><tr><td>NIST SP 系列</td><td>详细控制清单（SP 800-53, 800-61, 800-171 等）</td><td>政府 / 企业</td></tr><tr><td>CIS Controls</td><td>20 个关键安全控制</td><td>企业信息安全落地</td></tr><tr><td>ISO 31000</td><td>风险管理指南</td><td>企业整体风险</td></tr><tr><td>ISO 22301</td><td>业务连续性管理</td><td>企业关键业务</td></tr></tbody></table><hr><h2 id="六、不同框架本质对比-ciso-视角" tabindex="-1"><a class="header-anchor" href="#六、不同框架本质对比-ciso-视角" aria-hidden="true">#</a> 六、不同框架本质对比（CISO 视角）</h2><table><thead><tr><th>框架</th><th>性质</th><th>法律强制</th><th>核心目标</th><th>输出形式</th></tr></thead><tbody><tr><td>SOC 2</td><td>审计</td><td>市场强制</td><td>信任服务原则</td><td>审计报告</td></tr><tr><td>GDPR</td><td>法律</td><td>法律强制</td><td>个人数据保护</td><td>合规报告 / 罚款风险</td></tr><tr><td>等保 2.0</td><td>国家制度</td><td>法律 + 监管</td><td>信息系统等级保护</td><td>备案 / 检查 / 下线整改</td></tr><tr><td>NIST</td><td>方法论</td><td>不强制</td><td>安全体系与风险管理</td><td>内部控制 / 实践指南</td></tr></tbody></table><blockquote><p>核心理解：</p><ul><li><strong>SOC 2 = 审计 → 信任背书</strong></li><li><strong>GDPR = 法律 → 数据保护底线</strong></li><li><strong>等保 = 监管 → 国内准入门槛</strong></li><li><strong>NIST = 方法论 → 内功安全设计</strong></li></ul></blockquote><hr><h2 id="七、全球化企业安全合规策略" tabindex="-1"><a class="header-anchor" href="#七、全球化企业安全合规策略" aria-hidden="true">#</a> 七、全球化企业安全合规策略</h2><ol><li><strong>国内系统</strong></li></ol><ul><li>等保 2.0（三级或以上）</li><li>ISO 27001（国际背书可选）</li></ul><ol start="2"><li><strong>海外 SaaS / B2B</strong></li></ol><ul><li>SOC 2 Type II</li><li>GDPR（欧盟用户）</li><li>CCPA / LGPD / HIPAA（根据地区和行业）</li></ul><ol start="3"><li><strong>技术体系内部建设</strong></li></ol><ul><li>NIST CSF / SP 系列 → 构建安全架构与流程</li><li>CIS Controls → 安全落地操作</li></ul><ol start="4"><li><strong>行业准入</strong></li></ol><ul><li>PCI DSS → 支付安全</li><li>TISAX → 汽车行业</li><li>HITRUST → 医疗行业</li></ul><hr><h2 id="八、ciso-视角总结" tabindex="-1"><a class="header-anchor" href="#八、ciso-视角总结" aria-hidden="true">#</a> 八、CISO 视角总结</h2><ul><li><strong>方法论（NIST / CIS）</strong> → 内部安全体系建设</li><li><strong>管理体系（ISO 27001 / ISO 27701）</strong> → 全面安全管理</li><li><strong>审计 / 信任背书（SOC 2 / SOC 1 / CSA STAR）</strong> → 对外证明</li><li><strong>法律与监管（GDPR / 等保 / HIPAA / FISMA）</strong> → 法律风险控制</li><li><strong>行业标准（PCI DSS / TISAX / HITRUST）</strong> → 行业准入与客户信任</li></ul><blockquote><p>真正成熟的企业安全治理，是<strong>内功 + 合规 + 信任背书 + 行业准入</strong>四层结合。</p></blockquote>',32),l=[h];function i(o,n){return d(),r("div",null,l)}const S=t(e,[["render",i],["__file","iso.html.vue"]]);export{S as default};
